INSERT INTO `dulieu` (`id`, `data_type`, `data_content`, `created_at`, `updated_at`, `dulieu_nguoiCapNhat`) VALUES
(1, 'gioithieu_panel', 'XTech là công ty chuyên Phát triển ứng dụng và cung cấp giải pháp công nghệ kết nối thông minh trong lĩnh vực CNTT Với năng lực và kinh nghiệm hoạt động trong lĩnh vực CNTT, XTech không ngừng phát triển về công nghệ, nâng cao chất lượng sản phẩm và dịch vụ hướng đến sự hài lòng của khách hàng. Góp phần thúc đẩy ngành CNTT của đất nước theo hướng CNH-HĐH.', '2019-05-24 10:31:20', '0000-00-00 00:00:00', NULL),
(2, 'gioithieu_kinknghiem_chuyenmon', 'XTech với đội ngữ nhân lực nhiều năm kinh nghiệm trong lĩnh vực công nghệ số , chính lòng tin và sự hài lòng của khách hàng đã làm nền tảng cho công ty phát triển đón đầu những xu thế công nghệ mới.', '2019-05-24 08:08:43', '0000-00-00 00:00:00', NULL),
(3, 'gioithieu_vecongdong', 'Những hoạt động thiện nguyện do công ty XTech tổ chức nhằm giúp đỡ người dân khó khăn ở các huyện vùng cao.', '2019-05-24 08:08:43', '0000-00-00 00:00:00', NULL),
(4, 'duan_thanhtuu', 'Chúng tôi sẽ cho bạn thấy chính sự nỗ lực không ngừng,lòng đam mê,nhiệt huyết,sức mạnh tập thể đã giúp chúng tôi đạt được nhiều thành tựu.', '2019-05-24 08:08:43', '0000-00-00 00:00:00', NULL),
(5, 'vechungtoi_diemxuatphat', 'Xtech với đội ngữ nhân lực nhiều năm kinh nghiệm trong lĩnh vực công nghệ số,được thành lập chính thức vào năm 2017,với sự nỗ lực, theo đuổi đam mê cùng với lòng tin và sự hài lòng của khách hàng đã làm nền tảng cho công ty phát triển đón đầu những xu thế', '2019-05-24 08:08:43', '0000-00-00 00:00:00', NULL),
(6, 'vechungtoi_dennay', 'Chúng tôi đã làm việc với nhiều doanh nghiệp lớn nhỏ trong và ngoài nước về lĩnh vực công nghệ phần mềm và công nghệ số hóa.', '2019-05-24 08:08:43', '0000-00-00 00:00:00', NULL),
(7, 'vechungtoi_muctieu', 'Chúng tôi luôn đặt tinh thần trách nhiệm lên hàng đầu, lấy trách nhiệm cao làm tiêu chí để phấn đấu làm việc tốt nhất. Với mục tiêu và định hướng rõ ràng chúng tôi tin rằng mình sẽ đáp ứng được mọi tiêu chí khó khăn nhất của mọi đối tác.', '2019-05-24 08:08:43', '0000-00-00 00:00:00', NULL),
(15, 'khachhang', '<p>oooooooooooooooo</p>\r\n\r\n<p>&nbsp;</p>', '2019-05-24 11:02:19', '2019-05-24 04:02:19', 'Thông');

